mapper.insert:
INSERT into tbl_reply(
						RNO
						,BNO
						,REPLY
						,REPLYER
) values (
						seq_reply.nextval
						,#{bno}
						,#{reply}
						,#{replyer}
)

mapper.update:
UPDATE tbl_reply
REPLY = #{reply}, UPDATEDATE = sysdate
WHERE RNO = #{rno}

mapper.select:
select count(*)
from tbl_reply
where RNO= #{rno}
